function openNav() {
    document.getElementById("mySidenav").style.width = "350px";
}

function closeNav() {
    document.getElementById("mySidenav").style.width = "0";
}


$(document).ready(function () {
    var navClose = true;
    $(".nav-link").click(function () {
        if (navClose) {
           $("#mySidenav").css("width", "0")
        }
    });
});


var navbar = document.getElementById("menu");
// var sticky = navbar.offsetTop;

function myFunction() {
    if (window.pageYOffset >= 200) {
        navbar.classList.add("sticky")
    } else {
        navbar.classList.remove("sticky");
    }
}


$(document).scroll(function () {
    var currY = $(this).scrollTop();
    $("#about-us").css("display", "none");
    $(".intro-text").css("display", "none");

    if (currY >= $('.stage-item_1').offset().top || $('.stage-item_1').offset() == 0) {
        $(".parallax").css("background-size", "50% 100%")
    } else {
      $(".parallax").css("background-size", "50% 100%")
    }
    if (currY >= $('.stage-item_2').offset().top || $('.stage-item_2').offset() == 0) {
        $(".parallax").css("background-size", "60% 100%");
        $(".logo-home").css("position", "fixed");
        $(".intro-text").css({"display":"block", "position": "fixed"});
    }else{
        $(".logo-home").css("position", "absolute");
    }

    if (currY >= $('.stage-item_3').offset().top || $('.stage-item_3').offset() == 0) {
        $(".parallax").css("background-size", "70% 100%")
    }  

    if (currY >= $('.stage-item_4').offset().top || $('.stage-item_4').offset() == 0) {
        $(".parallax").css("background-size", "80% 100%")
    } 

    if (currY >= $('.stage-item_5').offset().top || $('.stage-item_5').offset() == 0) {
        $(".parallax").css("background-size", "85% 100%")
    } 

    if (currY >= $('.stage-item_6').offset().top || $('.stage-item_6').offset() == 0) {
        $(".parallax").css("background-size", "90% 100%")
    } 
    
    if (currY >= $('.stage-item_7').offset().top || $('.stage-item_7').offset() == 0) {
        $(".parallax").css("background-size", "95% 100%");
        $(".logo-home").css("position", "absolute");
        $(".intro-text").css("display", "none");
    }  
    if (currY >= $('.stage-item_8').offset().top || $('.stage-item_8').offset() == 0) {
        $(".parallax").css("background-size", "100% 100%");
        $("#about-us").css("display", "block");
       
    }  
});





